import React, { useState, useEffect } from 'react';
import { Box, CssBaseline, Drawer, List, ListItem, ListItemIcon, ListItemText, AppBar, Toolbar, Typography, Button, Container, Paper, Dialog, DialogTitle, DialogContent, DialogActions, TextField } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import NetworkRoutesTable from './NetworkRoutesTable';
import { fetchRoutes, searchRoutes, exportRoutesCSV, addRoute, editRoute, deleteRoute, uploadKMZ, fetchRoute, uploadTestResults } from './api';
import SearchExportBar from './SearchExportBar';
import RouteFormDialog from './RouteFormDialog';
import DarkFiberModal from './DarkFiberModal';

const drawerWidth = 220;

function App() {
  const [openDetails, setOpenDetails] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formOpen, setFormOpen] = useState(false);
  const [formMode, setFormMode] = useState('add'); // 'add' or 'edit'
  const [deleteConfirmOpen, setDeleteConfirmOpen] = useState(false);
  const [detailsOpen, setDetailsOpen] = useState(false);
  const [detailsRow, setDetailsRow] = useState(null);
  const [darkFiberOpen, setDarkFiberOpen] = useState(false);
  const [darkFiberCircuitId, setDarkFiberCircuitId] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetchRoutes()
      .then(data => {
        setRows(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to fetch data');
        setLoading(false);
      });
  }, []);

  const handleMoreDetails = (row) => {
    setDetailsRow(row);
    setDetailsOpen(true);
  };

  const handleSearch = (filters) => {
    setLoading(true);
    searchRoutes(filters)
      .then(data => {
        setRows(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to search');
        setLoading(false);
      });
  };

  const handleExport = () => {
    exportRoutesCSV().then(res => {
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'network_routes.csv');
      document.body.appendChild(link);
      link.click();
      link.remove();
    });
  };

  const handleAdd = () => {
    setFormMode('add');
    setSelectedRow(null);
    setFormOpen(true);
  };

  const handleEdit = () => {
    setFormMode('edit');
    setFormOpen(true);
  };

  const handleDelete = () => {
    setDeleteConfirmOpen(true);
  };

  const handleFormClose = () => {
    setFormOpen(false);
  };

  const handleFormSubmit = async (values, file, testResultsFile) => {
    setLoading(true);
    try {
      if (formMode === 'add') {
        await addRoute(values);
        if (file) await uploadKMZ(values.circuit_id, file);
        if (testResultsFile) await uploadTestResults(values.circuit_id, testResultsFile);
      } else if (formMode === 'edit') {
        await editRoute(selectedRow.circuit_id, values);
        if (file) await uploadKMZ(selectedRow.circuit_id, file);
        if (testResultsFile) await uploadTestResults(selectedRow.circuit_id, testResultsFile);
      }
      const data = await fetchRoutes();
      setRows(data);
      setFormOpen(false);
      setSelectedRow(null);
    } catch (err) {
      setError('Failed to save data');
    }
    setLoading(false);
  };

  const handleDeleteConfirm = async () => {
    setLoading(true);
    try {
      await deleteRoute(selectedRow.circuit_id);
      const data = await fetchRoutes();
      setRows(data);
      setSelectedRow(null);
      setDeleteConfirmOpen(false);
    } catch (err) {
      setError('Failed to delete');
    }
    setLoading(false);
  };

  const handleDetailsSave = async (values) => {
    setLoading(true);
    try {
      await editRoute(detailsRow.circuit_id, { ...detailsRow, more_details: values.more_details });
      const data = await fetchRoutes();
      setRows(data);
      setDetailsOpen(false);
      setDetailsRow(null);
    } catch (err) {
      setError('Failed to save details');
    }
    setLoading(false);
  };

  const handleOpenDarkFiber = (circuitId) => {
    setDarkFiberCircuitId(circuitId);
    setDarkFiberOpen(true);
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar position="fixed" sx={{ zIndex: 1201 }}>
        <Toolbar>
          <Typography variant="h6" noWrap component="div">
            Network Routes Repository
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: 'auto' }}>
          <List>
            <ListItem button onClick={handleAdd}>
              <ListItemIcon><AddIcon /></ListItemIcon>
              <ListItemText primary="Add" />
            </ListItem>
            <ListItem button onClick={handleEdit} disabled={!selectedRow}>
              <ListItemIcon><EditIcon /></ListItemIcon>
              <ListItemText primary="Edit" />
            </ListItem>
            <ListItem button onClick={handleDelete} disabled={!selectedRow}>
              <ListItemIcon><DeleteIcon /></ListItemIcon>
              <ListItemText primary="Delete" />
            </ListItem>
          </List>
        </Box>
      </Drawer>
      <Box component="main" sx={{ flexGrow: 1, bgcolor: 'background.default', p: 3 }}>
        <Toolbar />
        <Container maxWidth="xl">
          <Paper sx={{ p: 2 }}>
            <Typography variant="h5" gutterBottom>Network Routes Table</Typography>
            <SearchExportBar onSearch={handleSearch} onExport={handleExport} />
            {loading ? (
              <div>Loading...</div>
            ) : error ? (
              <div style={{ color: 'red' }}>{error}</div>
            ) : (
              <NetworkRoutesTable
                rows={rows}
                onMoreDetails={handleMoreDetails}
                onSelectRow={setSelectedRow}
                selectedRow={selectedRow}
                onOpenDarkFiber={handleOpenDarkFiber}
              />
            )}
            {openDetails && (
              <div>More Details Popup for {selectedRow.circuit_id} (to be implemented)</div>
            )}
          </Paper>
        </Container>
      </Box>
      <RouteFormDialog
        open={formOpen}
        onClose={handleFormClose}
        onSubmit={handleFormSubmit}
        initialValues={formMode === 'edit' ? selectedRow : {}}
        isEdit={formMode === 'edit'}
      />
      <Dialog open={detailsOpen} onClose={() => setDetailsOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle>More Details</DialogTitle>
        <DialogContent>
          <TextField
            label="Notes"
            value={detailsRow ? detailsRow.more_details : ''}
            fullWidth
            multiline
            minRows={2}
            InputProps={{ readOnly: true, style: { fontSize: '0.35rem', lineHeight: 1.1, minHeight: 24 }} }
            InputLabelProps={{ style: { fontSize: '0.35rem' } }}
            sx={{ fontSize: '0.35rem', minHeight: 24 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDetailsOpen(false)}>Close</Button>
        </DialogActions>
      </Dialog>
      <Dialog open={deleteConfirmOpen} onClose={() => setDeleteConfirmOpen(false)}>
        <DialogTitle>Delete Route</DialogTitle>
        <DialogContent>Are you sure you want to delete {selectedRow?.circuit_id}?</DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteConfirmOpen(false)}>Cancel</Button>
          <Button onClick={handleDeleteConfirm} color="error" variant="contained">Delete</Button>
        </DialogActions>
      </Dialog>
      <DarkFiberModal
        open={darkFiberOpen}
        onClose={() => setDarkFiberOpen(false)}
        circuitId={darkFiberCircuitId}
      />
    </Box>
  );
}

export default App;
