import React from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button } from '@mui/material';

const columns = [
  { id: 'location_a', label: 'Location A' },
  { id: 'location_b', label: 'Location B' },
  { id: 'circuit_id', label: 'UCN' },
  { id: 'expected_latency', label: 'Expected Latency' },
  { id: 'live_latency', label: 'Live Latency' },
  { id: 'bandwidth', label: 'Bandwidth' },
  { id: 'underlying_carrier', label: 'Underlying Carrier' },
  { id: 'cable_system', label: 'Cable System' },
  { id: 'is_special', label: 'Special/ULL' },
  { id: 'kmz_file_path', label: 'KMZ File' },
  { id: 'test_results_link', label: 'Test Results' },
  { id: 'outage_tickets_last_30d', label: 'Outage Tickets (30d)' },
  { id: 'maintenance_tickets_last_30d', label: 'Maintenance Tickets (30d)' },
  { id: 'more_details', label: 'More Details' },
];

const textCellStyle = {
  maxWidth: 220,
  maxHeight: 80,
  overflow: 'auto',
  whiteSpace: 'pre-line',
  wordBreak: 'break-word',
  padding: '6px 8px',
};

function NetworkRoutesTable({ rows, onMoreDetails, onSelectRow, selectedRow, onOpenDarkFiber }) {
  return (
    <TableContainer component={Paper}>
      <Table size="small">
        <TableHead>
          <TableRow>
            {columns.map(col => (
              <TableCell key={col.id}>{col.label}</TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.circuit_id}
              hover
              selected={selectedRow && selectedRow.circuit_id === row.circuit_id}
              onClick={() => onSelectRow && onSelectRow(row)}
              style={{ cursor: onSelectRow ? 'pointer' : 'default' }}
            >
              {columns.map(col => {
                if (col.id === 'more_details') {
                  return (
                    <TableCell key={col.id} style={textCellStyle}>
                      <Button variant="outlined" size="small" onClick={e => { e.stopPropagation(); onMoreDetails(row); }}>
                        More Details
                      </Button>
                    </TableCell>
                  );
                }
                if (col.id === 'bandwidth' && row.bandwidth === 'Dark Fiber') {
                  return (
                    <TableCell key={col.id} style={textCellStyle}>
                      <Button variant="contained" size="small" color="secondary" onClick={e => { e.stopPropagation(); onOpenDarkFiber(row.circuit_id); }}>
                        Dark Fiber Details
                      </Button>
                    </TableCell>
                  );
                }
                // For text-heavy columns, apply scrollable style
                const textHeavy = [
                  'test_results_link',
                  'cable_system',
                  'underlying_carrier',
                  'location_a',
                  'location_b',
                  'bandwidth',
                  'more_details',
                ];
                return (
                  <TableCell key={col.id} style={textHeavy.includes(col.id) ? textCellStyle : {}}>
                    {col.id === 'is_special' ? (row[col.id] ? 'Yes' : 'No') : row[col.id]}
                  </TableCell>
                );
              })}
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

export default NetworkRoutesTable;
