import axios from 'axios';

const API_BASE = 'http://localhost:4000';

export const fetchRoutes = () => axios.get(`${API_BASE}/network_routes`).then(res => res.data);

export const searchRoutes = (params) => axios.get(`${API_BASE}/network_routes_search`, { params }).then(res => res.data);

export const exportRoutesCSV = () => axios.get(`${API_BASE}/network_routes_export`, { responseType: 'blob' });

export const addRoute = (data) => axios.post(`${API_BASE}/network_routes`, data);

export const editRoute = (circuit_id, data) => axios.put(`${API_BASE}/network_routes/${circuit_id}`, data);

export const deleteRoute = (circuit_id) => axios.delete(`${API_BASE}/network_routes/${circuit_id}`);

export const uploadKMZ = (circuit_id, file) => {
  const formData = new FormData();
  formData.append('kmz_file', file);
  return axios.post(`${API_BASE}/network_routes/${circuit_id}/upload_kmz`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });
};

export const fetchRoute = (circuit_id) => axios.get(`${API_BASE}/network_routes/${circuit_id}`).then(res => res.data);

export const getDarkFiberDetails = (circuit_id) => axios.get(`${API_BASE}/dark_fiber_details/${circuit_id}`).then(res => res.data);
export const addDarkFiberDetail = (data) => axios.post(`${API_BASE}/dark_fiber_details`, data);
export const editDarkFiberDetail = (id, data) => axios.put(`${API_BASE}/dark_fiber_details/${id}`, data);
export const deleteDarkFiberDetail = (id) => axios.delete(`${API_BASE}/dark_fiber_details/${id}`);

export const uploadTestResults = (circuit_id, file) => {
  const formData = new FormData();
  formData.append('test_results_file', file);
  return axios.post(`${API_BASE}/network_routes/${circuit_id}/upload_test_results`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });
};

export const downloadTestResults = (circuit_id) => axios.get(`${API_BASE}/network_routes/${circuit_id}/download_test_results`, { responseType: 'blob' });
